import React, { useState, useEffect } from "react";
import AddProduct from "./AddProduct";
import ProductList from "./ProductList";

const API_URL = "http://localhost:5000/products";

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [editIndex, setEditIndex] = useState(null);

  // Fetch products from JSON Server
  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.log(err));
  }, []);

  const handleAddProduct = (prod) => {
    fetch(API_URL, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(prod),
    })
      .then((res) => res.json())
      .then((data) => setProducts([...products, data]))
      .catch((err) => console.log(err));
  };

  const handleDeleteProduct = (index) => {
    const product = products[index];
    fetch(`${API_URL}/${product.id}`, { method: "DELETE" })
      .then(() => {
        const newProducts = products.filter((_, i) => i !== index);
        setProducts(newProducts);
        if (editIndex === index) setEditIndex(null);
      })
      .catch((err) => console.log(err));
  };

  const handleEditProduct = (index) => setEditIndex(index);

  const handleUpdateProduct = (updatedProd) => {
    const product = products[editIndex];
    fetch(`${API_URL}/${product.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedProd),
    })
      .then((res) => res.json())
      .then((data) => {
        const newProducts = [...products];
        newProducts[editIndex] = data;
        setProducts(newProducts);
        setEditIndex(null);
      })
      .catch((err) => console.log(err));
  };

  const handleCancelEdit = () => setEditIndex(null);

  return (
    <div className="container my-5">
      <h2>Vendor Dashboard</h2>
      <p>Here you will see your products, orders, and sales summary.</p>

      <div className="row mt-4">
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h5>Total Products</h5>
              <p>{products.length}</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h5>Total Orders</h5>
              <p>5</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h5>Revenue</h5>
              <p>$250</p>
            </div>
          </div>
        </div>
      </div>

      <div className="mt-5">
        <AddProduct
          onAdd={handleAddProduct}
          editProduct={editIndex !== null ? products[editIndex] : null}
          onUpdate={handleUpdateProduct}
          onCancel={handleCancelEdit}
        />
        <ProductList
          products={products}
          onDelete={handleDeleteProduct}
          onEdit={handleEditProduct}
        />
      </div>
    </div>
  );
};

export default Dashboard;
